using System;\nusing System.Drawing;\nusing WebKit;\n\npublic class HtmlToImageConverter\n{\n    private static void Main(string[] args)\n    {\n        var webKitBrowser = new WebKit.WebKitBrowser();\n        \n        // Load the HTML content into the browser control\n        webKitBrowser.LoadHtml("<html><body><h1>Hello World!</h1></body></html>");\n\n        // Wait for the page to load completely\n        while (webKitBrowser.IsLoading)\n        {\n            System.Threading.Thread.Sleep(100);\n        }\n\n        // Get the size of the loaded document\n        var documentSize = webKitBrowser.Document.MainFrame.InnerText.Length;\n\n        // Render the content to a bitmap\n        using (var bitmap = new Bitmap(documentSize, 600))\n        {\n            webKitBrowser.DrawToBitmap(bitmap, new Rectangle(0, 0, documentSize, 600));\n\n            // Save the bitmap to an image file\n            bitmap.Save("output.png", System.Drawing.Imaging.ImageFormat.Png);\n        }\n\n        Console.WriteLine("Image saved as 'output.png'");\n    }\n}