public static string Iso8859ToUtf8(string isoFilePath, string utf8FilePath)\n{\n    // Read all bytes from the ISO-8859-1 encoded file\n    byte[] isoBytes = File.ReadAllBytes(isoFilePath);\n\n    // Convert ISO-8859-1 bytes to UTF-8 bytes\n    Encoding isoEncoding = Encoding.GetEncoding("ISO-8859-1");\n    Encoding utf8Encoding = Encoding.UTF8;\n    string decodedText = isoEncoding.GetString(isoBytes);\n    byte[] utf8Bytes = utf8Encoding.GetBytes(decodedText);\n\n    // Write the UTF-8 bytes to a new file\n    File.WriteAllBytes(utf8FilePath, utf8Bytes);\n\n    return utf8Encoding.GetString(utf8Bytes);\n}