using System;\nusing System.Collections.Generic;\n\nclass Program\n{\n    static void Main()\n    {\n        List<string> myList = new List<string>() { "Apple", "Banana", "Cherry" };\n        \n        // Call the ForEach method with a lambda expression\n        CustomForEach(myList, (item) => Console.WriteLine(item.ToString()));\n    }\n\n    // Method that takes a List<T> and an Action<T>\n    static void CustomForEach<T>(List<T> list, Action<T> action)\n    {\n        foreach (T item in list)\n        {\n            action(item);\n        }\n    }\n}