class Program\n{\n    static readonly string configFileName = "email.config";\n\n    static int Main(string[] args)\n    {\n        try\n        {\n            var fullPath = System.IO.Path.Combine(AppDomain.CurrentDomain.BaseDirectory, configFileName);\n            var configMap = new ExeConfigurationFileMap { ExeConfigFilename = fullPath };\n            Configuration externalConfig = ConfigurationManager.OpenMappedExeConfiguration(configMap, ConfigurationUserLevel.None);\n        }\n        catch (Exception e)\n        {\n            Console.WriteLine(e.Message);\n            return 1;\n        }\n    }\n}