using OpenNETCF.PocketOutlook;\nusing System;\n\npublic class SmsAccess {\n    public void ListSmsMessages() {\n        using (var ms = new MessagingStore()) {\n            var inbox = ms.SmsInbox;\n\n            foreach (SmsMessage msg in inbox) {\n                Console.WriteLine("From: {0}", msg.Sender);\n                Console.WriteLine("Date: {0}", msg.TimeStamp);\n                Console.WriteLine("Message: {0}", msg.BodyText);\n                Console.WriteLine();\n            }\n        }\n    }\n}