// Example of calling Microsoft Graph to get user presence\npublic class GraphService\n{\n    private readonly IGraphServiceClient _graphClient;\n\n    public GraphService(IGraphServiceClient graphClient)\n    {\n        _graphClient = graphClient;\n    }\n\n    public async Task<IUserPresence> GetUserPresence(string userId)\n    {\n        return await _graphClient.Users[userId].Presence.Request().GetAsync();\n    }\n}