// Example of managing presence with Redis\npublic class PresenceService\n{\n    private readonly IDatabase _redis;\n\n    public PresenceService(IDatabase redis)\n    {\n        _redis = redis ?? throw new ArgumentNullException(nameof(redis));\n    }\n\n    public async Task SetUserOnline(string userId)\n    {\n        await _redis.StringSetAsync($"online:{userId}", "true", TimeSpan.FromMinutes(10)); // Set timeout for automatic offline detection\n    }\n\n    public async Task<bool> IsUserOnline(string userId)\n    {\n        return (await _redis.StringGetAsync($"online:{userId}")).ToString() == "true";\n    }\n}