amespace MyNamespace\n{\n    public enum Color\n    {\n        Red,\n        Green,\n        Blue\n    }\n\n    public class Car\n    {\n        private string model;\n        private int year;\n        private Color color;\n\n        public Car(string model, int year, Color color)\n        {\n            this.model = model;\n            this.year = year;\n            this.color = color;\n        }\n\n        public void DisplayInfo()\n        {\n            Console.WriteLine($"Model: {model}, Year: {year}, Color: {color}");\n        }\n    }\n}