public class RouteConfig\n{\n    public static void RegisterRoutes(RouteCollection routes)\n    {\n        routes.IgnoreRoute("{resource}.axd/{*pathInfo}");\n\n        // Route for plural "bookmarks"\n        routes.MapRoute(\n            name: "Bookmarks",\n            url: "bookmarks",\n            defaults: new { controller = "Bookmark", action = "Index" }\n        );\n\n        // Route for singular "bookmark/{id}"\n        routes.MapRoute(\n            name: "BookmarkDetail",\n            url: "bookmark/{bookmarkId}",\n            defaults: new { controller = "Bookmark", action = "Details" },\n            constraints: new { bookmarkId = @"\d+" }  // Ensures that the URL has a numeric id\n        );\n\n        routes.MapRoute(\n            name: "Default",\n            url: "{controller}/{action}/{id}",\n            defaults: new { controller = "Home", action = "Index", id = UrlParameter.Optional }\n        );\n    }\n}