using Microsoft.WindowsMobile.PocketOutlook;\nusing Microsoft.WindowsMobile.Forms;\n\npublic void DisplaySmsMessage(ItemId messageId)\n{\n    // Initialize the Outlook session\n    using (OutlookSession outlook = new OutlookSession())\n    {\n        // Retrieve the messages from the SMS folder\n        MessageCollection smsMessages = outlook.SentItems;\n\n        // Find the message by its ID\n        if (smsMessages.Contains(messageId))\n        {\n            SmsMessage message = (SmsMessage)outlook.GetItem(messageId);\n\n            // Display the compose form with pre-filled content\n            SmsComposeForm composeForm = new SmsComposeForm();\n            composeForm.To.Add(message.Recipients[0].Address);\n            composeForm.Body.Text = message.BodyText;\n            composeForm.CanChangeDestination = false; // Prevent changing recipient\n\n            // Show the form, and handle user interaction\n            composeForm.ShowDialog();\n        }\n    }\n}