using Microsoft.WindowsMobile.PocketOutlook;\n\npublic class OutlookAccess {\n    public void ListEmails() {\n        try {\n            // Create a new instance of the Application object.\n            using (Application outlookApp = new Application()) {\n                // Get an instance of the Inbox folder.\n                Folder inboxFolder = outlookApp.Inbox;\n                \n                // Loop through all items in the Inbox folder.\n                foreach (Item item in inboxFolder.Items) {\n                    if (item is EmailMessage) {\n                        EmailMessage email = (EmailMessage)item;\n                        Console.WriteLine("Subject: {0}", email.Subject);\n                        Console.WriteLine("Sender: {0}", email.Sender.DisplayName);\n                        Console.WriteLine("Body: {0}", email.BodyText);\n                        Console.WriteLine();\n                    }\n                }\n            }\n        } catch (Exception ex) {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n    }\n}