protected void myTable_Init(object sender, EventArgs e)\n{\n    TableRow row = new TableRow();\n    for (int i = 1; i < 11; i++)\n    {\n        LinkButton linkButton = new LinkButton();\n        linkButton.Text = i.ToString();\n        linkButton.ClientIDMode = System.Web.UI.ClientIDMode.Static; // Ensure client ID does not change\n        linkButton.Attributes.Add("onclick", "document.getElementById('" + selectedLinkId.ClientID + "').value = '" + linkButton.ClientID + "';");\n        linkButton.CommandArgument = i.ToString();\n\n        AddLinkButtonToRow(linkButton, row);\n    }\n    myTable.Rows.Add(row);\n}\n\nprivate void AddLinkButtonToRow(LinkButton linkButton, TableRow row)\n{\n    TableCell cell = new TableCell();\n    cell.Controls.Add(linkButton);\n    row.Cells.Add(cell);\n}