using System;\nusing System.Windows.Forms;\n\nclass Program {\n    [STAThread]\n    static void Main(string[] args) {\n        Console.WriteLine("Copy a small amount of text from a browser, then press enter.");\n        Console.ReadLine();\n\n        var text = Clipboard.GetText();\n        Console.WriteLine();\n        Console.WriteLine("--->The clipboard as Text:");\n        Console.WriteLine(text);\n\n        Console.WriteLine();\n        Console.WriteLine("--->Rewriting clipboard with the same CF_HTML data.");\n        \n        string htmlContent;\n        if (Clipboard.ContainsText(TextDataFormat.Html)) {\n            htmlContent = Clipboard.GetText(TextDataFormat.Html);\n            Clipboard.SetText(htmlContent, TextDataFormat.Html);\n\n            var text2 = Clipboard.GetText();\n            Console.WriteLine();\n            Console.WriteLine("--->The clipboard as Text:");\n            Console.WriteLine(text2);\n\n            bool isSameHtml = (htmlContent == Clipboard.GetText(TextDataFormat.Html));\n            Console.WriteLine();\n            Console.WriteLine("Is HTML content the same: " + isSameHtml);\n        } else {\n            Console.WriteLine("Clipboard does not contain CF_HTML data.");\n        }\n\n        Console.WriteLine();\n        Console.WriteLine("Press enter to exit.");\n        Console.ReadLine();\n    }\n}