using System;\nusing System.Security.Cryptography.X509Certificates;\n\nclass Program\n{\n    static void Main()\n    {\n        // Define the store name and location for issued certificates on a CA.\n        string storeName = StoreName.CertificateAuthority.ToString();\n        StoreLocation storeLocation = StoreLocation.LocalMachine;\n\n        using (X509Store store = new X509Store(storeName, storeLocation))\n        {\n            try\n            {\n                // Open the store with Read access rights.\n                store.Open(OpenFlags.ReadOnly);\n\n                // Get all certificates in the store.\n                X509Certificate2Collection certs = store.Certificates;\n\n                foreach (X509Certificate2 cert in certs)\n                {\n                    Console.WriteLine($"Subject: {cert.Subject}");\n                    Console.WriteLine($"Issuer: {cert.Issuer}");\n                    Console.WriteLine($"Thumbprint: {cert.Thumbprint}");\n                    Console.WriteLine($"Not Before: {cert.NotBefore}");\n                    Console.WriteLine($"Not After: {cert.NotAfter}");\n                    Console.WriteLine();\n                }\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"An error occurred: {ex.Message}");\n            }\n        }\n    }\n}