using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string fileName = @"C:\path\to\your\file.txt";\n        string destinationDirPath = @"C:\destination\directory";\n\n        FileInfo newFile = new FileInfo(fileName);\n        \n        // Remove the ReadOnly attribute if it is set\n        if (newFile.IsReadOnly)\n        {\n            newFile.IsReadOnly = false;\n        }\n\n        try\n        {\n            File.Copy(newFile.FullName, Path.Combine(destinationDirPath, newFile.Name), true);\n        }\n        catch (UnauthorizedAccessException ex)\n        {\n            Console.WriteLine($"Access denied: {ex.Message}");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n    }\n}