using System.IO;\nusing iTextSharp.text.pdf;\nusing iTextSharp.text.pdf.parser;\n\npublic string ConvertPdfToText(byte[] pdfBytes)\n{\n    using (var memoryStream = new MemoryStream(pdfBytes))\n    using (var reader = new PdfReader(memoryStream))\n    {\n        var textBuilder = new StringBuilder();\n        for (var i = 1; i <= reader.NumberOfPages; i++)\n        {\n            string pageText = PdfTextExtractor.GetTextFromPage(reader, i);\n            textBuilder.Append(pageText);\n        }\n        return textBuilder.ToString();\n    }\n}\n\n// Usage inside your file upload logic\nbyte[] uploadedFileBytes = GetUploadedFileBytes(); // Implement this method to retrieve bytes from the uploaded file.\nstring extractedText = ConvertPdfToText(uploadedFileBytes);\nSaveTextAndPdfInDatabase(extractedText, uploadedFileBytes); // Ensure you have this method to save data in DB.