using System;\nusing System.Windows.Forms;\n\npublic class MainForm : Form\n{\n    private FlowLayoutPanel flowLayoutPanel;\n    private VScrollBar vScrollBar;\n\n    public MainForm()\n    {\n        // Initialize the FlowLayoutPanel\n        flowLayoutPanel = new FlowLayoutPanel\n        {\n            Dock = DockStyle.Left,\n            AutoScroll = true, // Enables scrolling automatically\n            Width = 300\n        };\n\n        // Example of adding custom user controls to the panel\n        for (int i = 0; i < 20; i++)\n        {\n            var itemControl = new CustomItemUserControl();\n            flowLayoutPanel.Controls.Add(itemControl);\n        }\n\n        Controls.Add(flowLayoutPanel);\n    }\n}\n\npublic class CustomItemUserControl : UserControl\n{\n    public CustomItemUserControl()\n    {\n        // Setup your custom user control here\n        Width = 280;\n        Height = 100; // Example size for demonstration\n\n        var label = new Label { Text = "Custom Item", Dock = DockStyle.Top };\n        Controls.Add(label);\n    }\n}