// Simple example using MEF\n[Export]\npublic class MyService : IMyService { ... }\n\npublic class Consumer \n{\n    [Import]\n    public IMyService Service { get; set; }\n}\n\nvar catalog = new AggregateCatalog();\ncatalog.Catalogs.Add(new AssemblyCatalog(typeof(MyService).Assembly));\nvar container = new CompositionContainer(catalog);\nConsumer consumer = new Consumer();\ncontainer.ComposeParts(consumer);\n\n// Simple example using Unity\npublic class MyService : IMyService { ... }\n\npublic class Consumer \n{\n    private readonly IMyService service;\n    \n    public Consumer(IMyService service)\n    {\n        this.service = service;\n    }\n}\n\nIUnityContainer container = new UnityContainer();\ncontainer.RegisterType<IMyService, MyService>();\nConsumer consumer = container.Resolve<Consumer>();