// Example Controller Action in ASP.NET MVC to serve map tiles locally\npublic ActionResult GetTile(string z, string x, string y)\n{\n    // Construct path to tile image based on z, x, y parameters\n    var tilePath = Server.MapPath($"~/maptiles/{z}/{x}/{y}.png");\n    \n    if (System.IO.File.Exists(tilePath))\n    {\n        return File(tilePath, "image/png");\n    }\n    \n    return HttpNotFound();\n}