using System.ServiceModel;\n\nclass Program\n{\n    static void Main()\n    {\n        using (ServiceHost host = new ServiceHost(typeof(PhoneService), \n            new Uri("net.pipe://localhost")))\n        {\n            host.AddServiceEndpoint(typeof(IPhoneService),\n                new NetNamedPipeBinding(), "phonestub");\n\n            host.Open();\n            Console.WriteLine("Phone Service is up and running.");\n            Console.ReadLine();\n        }\n    }\n}