using System;\nusing System.Data;\nusing IBM.Data.DB2; // Ensure you have this DB2 .NET provider installed\nusing System.Data.SqlClient;\n\npublic void TransferDB2MetadataToSQLServer(string db2ConnectionString, string sqlConnectionString, string schemaName, string tableName)\n{\n    var db2Connection = new Db2Connection(db2ConnectionString);\n    var sqlCommandText = $"SELECT tabschema AS TableSchema, tabname AS TableName, colname AS ColumnName, typename AS DataType, length AS MaxCharLength " +\n                         $"FROM syscat.columns WHERE tabschema='{schemaName}' AND tabname='{tableName}';";\n\n    using (var db2Command = new Db2Command(sqlCommandText, db2Connection))\n    {\n        db2Connection.Open();\n        var dataTable = new DataTable();\n\n        // Load data from DB2 into DataTable\n        dataTable.Load(db2Command.ExecuteReader());\n\n        // Use SqlBulkCopy to insert data into SQL Server table\n        using (var sqlConn = new SqlConnection(sqlConnectionString))\n        {\n            sqlConn.Open();\n            using (var bulkCopy = new SqlBulkCopy(sqlConn, SqlBulkCopyOptions.TableLock | SqlBulkCopyOptions.FireTriggers | SqlBulkCopyOptions.UseInternalTransaction, null))\n            {\n                // Map DB2 columns to SQL Server columns\n                bulkCopy.ColumnMappings.Add("TableSchema", "table_id");\n                bulkCopy.ColumnMappings.Add("TableName", "table_name"); // Assuming you have a table name column in all_table_information\n                bulkCopy.ColumnMappings.Add("ColumnName", "column_name");\n                bulkCopy.ColumnMappings.Add("DataType", "data_type");\n                bulkCopy.ColumnMappings.Add("MaxCharLength", "max_char_length");\n\n                bulkCopy.DestinationTableName = "[all_table_information]";\n                bulkCopy.WriteToServer(dataTable);\n            }\n        }\n    }\n}