using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string query = "SELECT one, two, three FROM table1, table2 WHERE X=Y";\n        string pattern = @"(?i)\bFROM\b\s+([^\s,]+)(?:\s*,\s*([^\s,]+))*";\n\n        Match match = Regex.Match(query, pattern);\n        if (match.Success)\n        {\n            for (int i = 1; i <= match.Groups.Count; i++)\n            {\n                if (!string.IsNullOrEmpty(match.Groups[i].Value))\n                {\n                    Console.WriteLine("Table Name: " + match.Groups[i].Value);\n                }\n            }\n        }\n    }\n}