using (OracleConnection conn = new OracleConnection(connectionString))\n{\n    try\n    {\n        conn.Open();\n        \n        // Set the default rollback segment for this session\n        using (OracleCommand cmdSetRollbackSegment = new OracleCommand("ALTER SESSION SET DEFAULT rollback_segment = MY_ROLLBACK_SEGMENT;", conn))\n        {\n            cmdSetRollbackSegment.ExecuteNonQuery();\n        }\n        \n        // Now begin your transaction\n        using (OracleTransaction tx = conn.BeginTransaction())\n        {\n            // Your transactional code here\n            \n            // Commit the transaction if all operations are successful\n            tx.Commit();\n        }\n    }\n    catch (Exception ex)\n    {\n        // Handle exceptions here\n        Console.WriteLine($"Error: {ex.Message}");\n    }\n}