using System;\nusing System.IO;\nusing System.Xml;\nusing System.Xml.XPath;\n\npublic partial class MainWindow : Window\n{\n    public MainWindow()\n    {\n        InitializeComponent();\n        \n        // Load the XML document into XmlDocument\n        XmlDocument doc = new XmlDocument();\n        doc.LoadXml("<Books><Book><Title>Book1</Title><Author>Author1</Author></Book><Book><Title>Book2</Title><Author>Author2</Author></Book></Books>");\n        \n        // Create an XPathNavigator from the XmlDocument\n        XPathNavigator navigator = doc.CreateNavigator();\n        \n        // Compile an XPath expression to select all titles\n        XPathExpression expr = navigator.Compile("/Books/Book/Title");\n        \n        // Evaluate the XPath query using the XPathNavigator\n        XPathNodeIterator iterator = navigator.Select(expr);\n        \n        // Iterate through selected nodes and output each title\n        while (iterator.MoveNext())\n        {\n            XPathNavigator node = iterator.Current;\n            Console.WriteLine(node.Value);  \n        }\n    }\n}