using System;\nusing System.Globalization;\n\npublic class TypeConverter {\n    public static object ConvertFrom(string valueToConvertFrom, Type convertToType) {\n        if (convertToType == typeof(bool)) return bool.Parse(valueToConvertFrom);\n        else if (convertToType == typeof(char)) return char.Parse(valueToConvertFrom);\n        // Handle parsing for DateTime separately due to varying formats\n        else if (convertToType == typeof(DateTime)) \n            return DateTime.ParseExact(valueToConvertFrom, "o", CultureInfo.InvariantCulture); // or any other format you expect\n        return Convert.ChangeType(valueToConvertFrom, convertToType, CultureInfo.InvariantCulture);\n    }\n\n    public static void Main() {\n        string value = "123";\n        \n        Type intType = typeof(int);\n        object resultInt = ConvertFrom(value, intType);\n        Console.WriteLine($"Converted to Int: {resultInt}");\n\n        Type dateTimeType = typeof(DateTime);\n        // Assuming the date time format is 2024-04-15T09:00:00.0000000\n        value = "2024-04-15T09:00:00.0000000";\n        object resultDateTime = ConvertFrom(value, dateTimeType);\n        Console.WriteLine($"Converted to DateTime: {resultDateTime}");\n    }\n}