// In BundleConfig.cs\npublic static void RegisterBundles(BundleCollection bundles)\n{\n    // Add a script bundle.\n    bundles.Add(new ScriptBundle("~/bundles/scripts").Include(\n        "~/Scripts/jquery-{version}.js",\n        "~/Scripts/custom.js"));\n    \n    // Add a style bundle.\n    bundles.Add(new StyleBundle("~/Content/css").Include(\n        "~/Content/site.css"));\n}