using System;\nusing System.Diagnostics;\n\npublic class TiffToPdfConverter\n{\n    public void ConvertTiffToPdf(string inputFilePath, string outputFilePath)\n    {\n        ProcessStartInfo start = new ProcessStartInfo();\n        start.FileName = @"C:\Program Files\gs\gs9.53.3\bin\gswin64c.exe"; // Path to Ghostscript\n        start.Arguments = $"-q -dbatch -dNOPAUSE -sDEVICE=pdfwrite -sOutputFile={outputFilePath} {inputFilePath}";\n        start.UseShellExecute = false;\n        start.RedirectStandardOutput = true;\n\n        using (Process process = Process.Start(start))\n        {\n            using (System.IO.StreamReader reader = process.StandardOutput)\n            {\n                string result = reader.ReadToEnd();\n                Console.WriteLine("Conversion Result: " + result);\n            }\n            process.WaitForExit();\n        }\n    }\n}