using System;\nusing System.ServiceProcess;\n\nclass Program : ServiceBase\n{\n    static void Main(string[] args)\n    {\n        var serviceName = "MyServiceName"; // Replace with your actual service name\n\n        if (Environment.UserInteractive)\n        {\n            Console.WriteLine("Running as a console application.");\n            RunAsConsole();\n        }\n        else\n        {\n            Console.WriteLine("Running as a service.");\n            ServiceBase[] ServicesToRun;\n            ServicesToRun = new ServiceBase[]\n                { \n                    new Program() \n                };\n            Run(ServicesToRun);\n        }\n    }\n\n    private static void RunAsConsole()\n    {\n        // Implement your console-specific logic here\n        Console.WriteLine("Executing in console mode...");\n    }\n\n    protected override void OnStart(string[] args)\n    {\n        // Service start-up logic\n        Console.WriteLine("Service started.");\n    }\n\n    protected override void OnStop()\n    {\n        // Service stop-logic\n        Console.WriteLine("Service stopped.");\n    }\n}