using System.IO;\nusing System.Web.UI;\n\n// Create a StringWriter to capture the output\nStringWriter myTextWriter = new StringWriter();\n\n// Use HtmlTextWriter to write HTML\nHtmlTextWriter myWriter = new HtmlTextWriter(myTextWriter);\n\n// Load your UserControl\nUserControl myControl = (UserControl)LoadControl("newUserControl.ascx");\n\n// Create an HtmlForm and manually add your control to it\nusing (var form = new HtmlForm())\n{\n    // Add the UserControl to the form\n    form.Controls.Add(myControl);\n    \n    // Render the form using your HtmlTextWriter, which will now include your control's output\n    myControl.Page = new Page();\n    ((IHttpHandler)myControl.Page).ProcessRequest(new HttpContext(System.Web.HttpContext.Current.Request, System.Web.HttpContext.Current.Response));\n    form.RenderControl(myWriter);\n}\n\n// Return the captured HTML as a string\nreturn myTextWriter.ToString();