using System;\nusing System.Net.Http;\nusing System.Text;\nusing System.Text.Json;\nusing System.Threading.Tasks;\n\npublic class RestClient\n{\n    private readonly HttpClient _httpClient;\n\n    public RestClient(HttpClient httpClient)\n    {\n        _httpClient = httpClient;\n    }\n\n    // GET request example\n    public async Task<T> GetAsync<T>(string url)\n    {\n        var response = await _httpClient.GetAsync(url);\n        response.EnsureSuccessStatusCode();\n        string responseBody = await response.Content.ReadAsStringAsync();\n        return JsonSerializer.Deserialize<T>(responseBody);\n    }\n\n    // POST request example\n    public async Task PostAsync<T>(string url, T data)\n    {\n        var json = JsonSerializer.Serialize(data);\n        var stringContent = new StringContent(json, Encoding.UTF8, "application/json");\n        var response = await _httpClient.PostAsync(url, stringContent);\n        response.EnsureSuccessStatusCode();\n    }\n}\n\n// Usage example\npublic class Program\n{\n    public static async Task Main()\n    {\n        using var httpClient = new HttpClient();\n        var client = new RestClient(httpClient);\n\n        // Example of getting data\n        var userData = await client.GetAsync<UserData>("https://api.example.com/user/1");\n\n        // Example of posting data\n        var newData = new { Name = "John Doe", Age = 30 };\n        await client.PostAsync("https://api.example.com/user", newData);\n    }\n}\n\npublic class UserData\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n    public int Age { get; set; }\n}