protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        var items = new List<Item>\n        {\n            new Item { Type = "Audio", Name = "Thing" },\n            new Item { Type = "Audio", Name = "Thing" },\n            new Item { Type = "Video", Name = "Thing" },\n            new Item { Type = "Picture", Name = "Thing" }\n        };\n\n        itemsRepeater.DataSource = items;\n        itemsRepeater.DataBind();\n    }\n}\n\nprivate string RenderItemType(object dataItem)\n{\n    var item = (Item)dataItem;\n\n    // Assuming your data source is ordered by Type\n    if (itemsRepeater.Items.Count == 0 || \n        ((Item)itemsRepeater.Items[itemsRepeater.Items.Count - 1].DataItem).Type != item.Type)\n    {\n        return $"<h3>{item.Type}</h3>";\n    }\n\n    return string.Empty;\n}\n\npublic class Item\n{\n    public string Type { get; set; }\n    public string Name { get; set; }\n}