using System;\nusing System.Threading;\n\nclass Program\n{\n    private static ManualResetEvent _quitEvent = new ManualResetEvent(false);\n\n    static void Main()\n    {\n        // Timer setup, adjust interval (in milliseconds) as needed\n        int interval = 5000; // 5 seconds\n        var timer = new System.Threading.Timer(TimerCallback, null, 0, interval);\n\n        Console.WriteLine("Timer started. Press Enter to exit.");\n\n        // Wait indefinitely while the timer operates in another thread\n        _quitEvent.WaitOne();\n\n        // Cleanup or additional code here if necessary before exiting\n    }\n\n    static void TimerCallback(object state)\n    {\n        // Perform your periodic actions here\n        Console.WriteLine($"Timer tick at {DateTime.Now}");\n    }\n}