using System;\nusing System.Data; // Required namespace for Data Classes\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = @"Data Source=|DataDirectory|\MyDbFile.sdf";\n        \n        Console.WriteLine("Initial Connection String: " + connectionString);\n        \n        // In a real application, this resolution would be handled automatically by the data provider.\n        string resolvedPath = AppDomain.CurrentDomain.GetData("DataDirectory").ToString();\n        string resolvedConnectionString = connectionString.Replace("|DataDirectory|", resolvedPath);\n        \n        Console.WriteLine("Resolved Connection String: " + resolvedConnectionString);\n    }\n}