protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Assuming you have a method to get the user's column preferences\n        var userPreferences = GetUserColumnPreferences();\n\n        foreach (var kvp in userPreferences)\n        {\n            SetColumnVisibility(kvp.Key, kvp.Value);\n        }\n\n        UserDataGrid.DataBind();\n    }\n}\n\nprivate bool GetColumnVisibility(string columnName)\n{\n    // Replace this with logic to retrieve the preference from the profile\n    var preferences = (Dictionary<string, bool>)Profile.GetPropertyValue("ColumnPreferences");\n    if (preferences != null && preferences.ContainsKey(columnName))\n    {\n        return preferences[columnName];\n    }\n    return true; // Default visibility\n}\n\nprivate void SetColumnVisibility(string columnName, bool visible)\n{\n    foreach (DataControlField column in UserDataGrid.Columns)\n    {\n        if (column.HeaderText == columnName)\n        {\n            column.Visible = visible;\n            break;\n        }\n    }\n}\n\n// Example method to simulate getting user preferences\nprivate Dictionary<string, bool> GetUserColumnPreferences()\n{\n    // This should actually retrieve the data from a database or profile provider\n    return new Dictionary<string, bool>\n    {\n        { "ID", true },\n        { "Name", false },\n        { "Email", true }\n    };\n}