class Program\n{\n    static void Main(string[] args)\n    {\n        Uri baseAddress = new Uri("http://localhost:9000/ServiceRegistry");\n\n        using (ServiceHost host = new ServiceHost(typeof(ServiceRegistry), baseAddress))\n        {\n            host.AddServiceEndpoint(typeof(IServiceRegistry),\n                new WSHttpBinding(), "ServiceRegistry");\n            \n            host.Open();\n            Console.WriteLine("The service is ready.");\n            Console.ReadLine();\n           \n            // Close the ServiceHost to shutdown the service.\n            host.Close();           \n        }\n    }\n}