using System.IO;\nusing System.Web.HttpRuntime; // Use HttpRuntime if outside MVC context\nusing System.Xml.Linq;\n\nnamespace Site1.Controllers\n{\n    [SkyArts.Models.Master]\n    public abstract class BaseController : Controller\n    {\n        protected XDocument ButtonsXmlDoc { get; set; }\n\n        public BaseController()\n        {\n            // Use HttpRuntime.AppDomainAppPath for non-MVC contexts or Server.MapPath for MVC controllers\n            string xmlFilePath = Path.Combine(HttpRuntime.AppDomainAppPath, "App_Data", "Buttons.xml");\n            ButtonsXmlDoc = XDocument.Load(xmlFilePath);\n        }\n    }\n}