[TestClass]\npublic class EmployeeControllerTests\n{\n    [TestMethod]\n    public void Index_ReturnsEmployeeDetails()\n    {\n        // Arrange\n        var employeeServiceMock = new Mock<IEmployeeService>();\n        employeeServiceMock.Setup(s => s.GetEmployeeById(It.IsAny<int>()))\n                           .Returns(new Employee { Id = 1, Name = "John Doe", Position = "Developer" });\n\n        var controller = new EmployeeController(employeeServiceMock.Object);\n\n        var requestModel = new EmployeeViewModel\n        {\n            EmployeeId = 1\n        };\n\n        // Act\n        var result = controller.Index(requestModel) as ViewResult;\n        var viewModel = result.Model as EmployeeViewModel;\n\n        // Assert\n        Assert.IsNotNull(viewModel);\n        Assert.AreEqual(1, viewModel.Id);\n        Assert.AreEqual("John Doe", viewModel.Name);\n        Assert.AreEqual("Developer", viewModel.Position);\n    }\n}