using System.Net.Mail;\nusing Microsoft.Practices.EnterpriseLibrary.ExceptionHandling;\nusing Microsoft.Practices.EnterpriseLibrary.Logging;\nusing Microsoft.Practices.EnterpriseLibrary.Logging.TraceListeners;\n\npublic void SendHtmlEmail()\n{\n    // Create a new MailMessageWrapper instance\n    MailAddress from = new MailAddress("from@example.com");\n    MailAddress to = new MailAddress("to@example.com");\n    string subject = "HTML Email Test";\n    string body = "<html><body><h1>Hello, World!</h1></body></html>";\n    \n    MailMessage mail = new MailMessage(from, to)\n    {\n        Subject = subject,\n        Body = body,\n        IsBodyHtml = true\n    };\n    \n    // Create a MailMessageWrapper from the MailMessage instance\n    MailMessageWrapper messageWrapper = new MailMessageWrapper(mail);\n    \n    try\n    {\n        // Send the email using the Logging Application Block\n        Logger.Write(messageWrapper, "Email Trace Listener");\n    }\n    catch (Exception ex)\n    {\n        ExceptionPolicy.HandleException(ex, "Email Policy");\n    }\n}