public class MathCollection<T> where T : IDoMaths<T>\n{\n    private List<T> _items = new List<T>();\n\n    public void Add(T item) => _items.Add(item);\n\n    public T Sum()\n    {\n        if (_items.Count == 0)\n            throw new InvalidOperationException("Cannot sum an empty collection.");\n\n        T total = default;\n        foreach (var item in _items)\n        {\n            total = total.Add(item);\n        }\n\n        return total;\n    }\n\n    // You can add other methods that use the Subtract method as needed\n}