string[] storeNames = { "My", "Root", "Trust" }; // Add other potentially relevant store names\nX509Certificate2Collection col = new X509Certificate2Collection();\n\nforeach (string storeName in storeNames)\n{\n    using (X509Store store = new X509Store(storeName, StoreLocation.LocalMachine))\n    {\n        store.Open(OpenFlags.ReadOnly);\n        X509Certificate2Collection certCol = store.Certificates.Find(X509FindType.FindBySerialNumber, "xxx", true);\n        col.AddRange(certCol);\n    }\n}\n\nif (col.Count == 0)\n{\n    using (X509Store store = new X509Store("My", StoreLocation.CurrentUser))\n    {\n        store.Open(OpenFlags.ReadOnly);\n        col = store.Certificates.Find(X509FindType.FindBySerialNumber, "xxx", true);\n    }\n}\n\nif (col.Count > 0)\n{\n    // Proceed with using the certificate\n}\nelse\n{\n    // Certificate not found or is indeed invalid\n}