using System;\nusing System.IO;\nusing System.Net;\nusing System.Web.Services;\n\n[WebService(Namespace = "http://yournamespace.com/SecureWeatherService/")]\npublic class SecureWeatherService : WebService\n{\n    [WebMethod]\n    public string GetWeatherData(string parameters)\n    {\n        try\n        {\n            // Construct the URL with the parameters received\n            string url = $"http://[apicode].api.wxbug.net/weatherservice.asmx/{parameters}";\n\n            // Create a web request to the WeatherBug service\n            WebRequest request = WebRequest.Create(url);\n            using (WebResponse response = request.GetResponse())\n            {\n                using (StreamReader reader = new StreamReader(response.GetResponseStream()))\n                {\n                    return reader.ReadToEnd();\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            // Log exception details here\n            return $"Error: {ex.Message}";\n        }\n    }\n}