using Microsoft.AspNetCore.Mvc;\nusing HtmlSanitizer; // Import HtmlSanitizer for sanitizing HTML\n\npublic class ContentController : Controller\n{\n    private readonly IContentRepository _contentRepository;\n\n    public ContentController(IContentRepository contentRepository)\n    {\n        _contentRepository = contentRepository;\n    }\n\n    [HttpGet]\n    public IActionResult Edit()\n    {\n        var model = new ContentEditModel { CurrentContent = _contentRepository.GetWelcomePageContent() };\n        return View(model);\n    }\n\n    [HttpPost]\n    public IActionResult Save(string content)\n    {\n        // Sanitize input before saving\n        HtmlSanitizer sanitizer = new HtmlSanitizer();\n        var safeHtmlContent = sanitizer.Sanitize(content);\n\n        _contentRepository.SaveWelcomePageContent(safeHtmlContent);\n        return RedirectToAction("Edit");\n    }\n}