using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        Process process = new Process();\n        ProcessStartInfo startInfo = new ProcessStartInfo\n        {\n            FileName = "xdiscbld.exe",\n            Arguments = "your arguments here",\n            RedirectStandardInput = true,\n            RedirectStandardOutput = true,\n            RedirectStandardError = true,\n            UseShellExecute = false, // This is crucial to redirect streams\n            CreateNoWindow = true    // Prevents a new window from being created\n        };\n\n        process.StartInfo = startInfo;\n\n        process.OutputDataReceived += (sender, args) =>\n        {\n            if (!string.IsNullOrEmpty(args.Data))\n            {\n                // Update your RichTextBox here with args.Data\n                // You might need to invoke this update onto the UI thread\n            }\n        };\n\n        process.ErrorDataReceived += (sender, args) =>\n        {\n            if (!string.IsNullOrEmpty(args.Data))\n            {\n                // Update your error handling mechanism here\n            }\n        };\n\n        process.Start();\n        process.BeginOutputReadLine();\n        process.BeginErrorReadLine();\n\n        process.WaitForExit();\n    }\n}