// Assuming your DataGridView is named dataGridView1 and your DataSource list is myList\nmyList.Add(new myObject()); // Add a new empty object to your data source\ndataGridView1.DataSource = null; // Reset DataSource to refresh the view\ndataGridView1.DataSource = myList; // Rebind the DataGridView with updated data\n\n// Set focus on the second cell of the newly added row\nint lastRowIndex = dataGridView1.Rows.Count - 2; // Subtract 2 because one index and another for header\ndataGridView1.FirstDisplayedScrollingRowIndex = lastRowIndex;\ndataGridView1.CurrentCell = dataGridView1.Rows[lastRowIndex].Cells[1]; // Focus on the second column\ndataGridView1.BeginEdit(true); // Optionally begin edit mode immediately