using System;\nusing System.IO;\n\npublic class DirectoryScanner\n{\n    public void ScanDirectories(string path)\n    {\n        try\n        {\n            foreach (var file in Directory.GetFiles(path))\n            {\n                Console.WriteLine(file);\n            }\n\n            // Recursively iterate through subdirectories.\n            foreach (var directory in Directory.GetDirectories(path))\n            {\n                ScanDirectories(directory); // Recursive call for each found directory\n            }\n        }\n        catch (UnauthorizedAccessException)\n        {\n            // Log the path that caused the exception or handle it as needed\n            Console.WriteLine($"Access denied to: {path}");\n        }\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        DirectoryScanner scanner = new DirectoryScanner();\n        string basePath = "K:\\"; // Or any other base path provided by the user\n        scanner.ScanDirectories(basePath);\n    }\n}