public interface IContentRepository\n{\n    string GetWelcomePageContent();\n    void SaveWelcomePageContent(string content);\n}\n\n// Example implementation (InMemory for simplicity)\npublic class InMemoryContentRepository : IContentRepository\n{\n    private string _welcomeContent = "<p>Welcome to our site!</p>";\n\n    public string GetWelcomePageContent()\n    {\n        return _welcomeContent;\n    }\n\n    public void SaveWelcomePageContent(string content)\n    {\n        // Store in DB, example implementation is storing in memory.\n        _welcomeContent = content;\n    }\n}