using System;\nusing System.Net;\nusing System.Text.RegularExpressions;\n\npublic class VersionChecker\n{\n    public static string GetLatestVersion(string baseUri)\n    {\n        HttpWebRequest request = (HttpWebRequest)WebRequest.Create(baseUri);\n        HttpWebResponse response = (HttpWebResponse)request.GetResponse();\n\n        using (System.IO.Stream stream = response.GetResponseStream())\n        {\n            using (System.IO.StreamReader reader = new System.IO.StreamReader(stream))\n            {\n                string html = reader.ReadToEnd();\n                return ParseLatestVersionFromHtml(html);\n            }\n        }\n    }\n\n    private static string ParseLatestVersionFromHtml(string html)\n    {\n        string versionPattern = @"(\d+)\.(\d+)\.(\d+)";\n        MatchCollection matches = Regex.Matches(html, versionPattern);\n\n        Version latestVersion = new Version(0, 0, 0);\n        foreach (Match match in matches)\n        {\n            Version currentVersion = new Version(int.Parse(match.Groups[1].Value), \n                                                   int.Parse(match.Groups[2].Value),\n                                                   int.Parse(match.Groups[3].Value));\n            if (currentVersion > latestVersion)\n            {\n                latestVersion = currentVersion;\n            }\n        }\n\n        return latestVersion.ToString();\n    }\n\n    public static void Main()\n    {\n        string baseUri = "http://example.com/folder/";\n        try\n        {\n            string latestVersion = GetLatestVersion(baseUri);\n            Console.WriteLine("Latest version: {0}", latestVersion);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error retrieving the latest version: {0}", ex.Message);\n        }\n    }\n}