using System.Threading.Tasks;\nusing PuppeteerSharp;\n\nclass Program\n{\n    public static async Task Main(string[] args)\n    {\n        // Initialize Puppeteer and launch a browser instance.\n        await new BrowserFetcher().DownloadAsync(BrowserFetcher.DefaultRevision);\n        var browser = await Puppeteer.LaunchAsync(new LaunchOptions\n        {\n            Headless = true // Run without GUI\n        });\n\n        // Open a new page.\n        var page = await browser.NewPageAsync();\n\n        // Navigate to the YouTube video.\n        string videoUrl = "https://www.youtube.com/watch?v=VIDEO_ID";\n        await page.GoToAsync(videoUrl);\n        \n        // Wait for the video element to load.\n        await page.WaitForSelectorAsync("#movie_player");\n\n        // Execute JavaScript to play the video and capture a frame.\n        await page.EvaluateFunctionAsync(@"async () => {\n            var video = document.querySelector('#movie_player video');\n            video.play();\n            await video.onloadedmetadata;\n            video.pause();\n        }");\n        \n        // Take a screenshot of the specific element (video).\n        await page.ScreenshotElementAsync("#movie_player video", "youtube-snapshot.png");\n\n        // Close the browser.\n        await browser.CloseAsync();\n\n        Console.WriteLine("Snapshot taken.");\n    }\n}