using System;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        // Connection string for your SQL Server instance\n        string connectionString = "Server=your_server;Database=your_database;Integrated Security=True;";\n        \n        // SQL command to create a view\n        string createViewQuery = @"\n            IF OBJECT_ID('dbo.YourNewView', 'V') IS NOT NULL\n                DROP VIEW dbo.YourNewView;\n            CREATE VIEW dbo.YourNewView AS\n            SELECT Column1, Column2 \n            FROM YourTable;";\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            try\n            {\n                connection.Open();\n                using (SqlCommand command = new SqlCommand(createViewQuery, connection))\n                {\n                    int result = command.ExecuteNonQuery();\n                    Console.WriteLine("View created successfully.");\n                }\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("An error occurred: " + ex.Message);\n            }\n        }\n    }\n}