using System;\nusing System.IO;\nusing System.Web.UI;\n\npublic string RenderUserControlToString(UserControl control)\n{\n    using (StringWriter sw = new StringWriter())\n    {\n        using (HtmlTextWriter tw = new HtmlTextWriter(sw))\n        {\n            control.RenderControl(tw);\n            return sw.ToString();\n        }\n    }\n}\n\n// Usage\nUserControl myControl = (UserControl)Page.LoadControl("~/PathToYourUserControl.ascx");\nmyControl.Controls.Add(new TextBox());\n\nstring strHTMLofControl = RenderUserControlToString(myControl);